(function () {    'use strict';    angular.module('App').service('PupilService', ['$http', PupilService]);    function PupilService ($http, $location) {        this.saveUrl= '/pupils/save';        this.errMsgs = {            name: 'Имя обязательно для ввода',            email: 'Почта обязательна для ввода',            date_birth: 'Дата рождения обязательна для ввода',            date_birth_format: 'Введите дату в формате ДД.ММ.ГГГГ',            level_id: 'Уровень обязателен для ввода'        };        this.pupil = {            name: '',            email: '',            date_birth: '',            level_id: ''        };        this.save = function (pupil) {            var self = this;            return $http.post(self.saveUrl, JSON.stringify(pupil));        }    }})();