(function () {    'use strict';    angular        .module('App')        .service('TeacherService', ['$http', TeacherService]);    function TeacherService ($http) {        this.saveUrl = '/teachers/save',        this.redirectUrl = '/teacher-list',        this.errMsgs = {            name: 'Имя обязательно для ввода',            phone: 'Телефон обязателен для ввода',            gender: 'Пол обязателен для ввода'        };        this.teacher = {            name: '',            phone: '',            gender: 1        };        this.save = function (teacher) {            var self=this;            return $http.post(self.saveUrl, JSON.stringify(teacher));        }    }})();